mapper.select:
SELECT s0.stock_code, s0.stock_name s0.lastDayCompare AS last_day_compare, s1.mean_ratio AS mean_ratio FROM ( SELECT t0.stock_code, t0.volume / t1.volume AS lastDayCompare FROM ( SELECT stock_code, volume, `close`, `open`, `high`, `low` FROM t_etf WHERE trade_date = #{currentDate} ) AS t0 -- 最后一个交易日 LEFT JOIN ( SELECT stock_code, volume, `close` FROM t_etf WHERE trade_date = #{preDate} ) AS t1 ON t0.stock_code = t1.stock_code -- 倒数第二个交易
WHERE t0.volume / t1.volume > 1.5 -- 成交量是前一天的2倍 and t0.volume > 10000000 ) s0 JOIN ( SELECT t0.volume / ( t1.mean ) AS mean_ratio, t0.stock_code FROM t_etf AS t0 LEFT JOIN ( SELECT SUM( volume ) / 3 AS mean,-- 获取最近3个工作日的成交量平均数 stock_code FROM t_etf WHERE volume is not null and trade_date IN ( SELECT trade_date FROM ( SELECT trade_date FROM t_etf WHERE stock_code = '512980' AND trade_date < #{currentDate} ORDER BY trade_date DESC LIMIT 3 ) AS t ) GROUP BY stock_code ) t1 ON t0.stock_code = t1.stock_code where t0.trade_date = #{currentDate} AND t1.mean != 0 AND t1.mean IS NOT NULL AND t0.volume / t1.mean > 1.5 and t0.volume > 10000000 -- 当日交易金额大于1000万
) s1 ON s0.stock_code = s1.stock_code;
